    public static List<Integer> findSubstring(String s, String[] words) {
        List<Integer> result=new ArrayList<>();
        int stepsize=words[0].length();
        Map<String,Integer> filtermap=new HashMap<>();
        for (String word : words) {
            filtermap.put(word,filtermap.getOrDefault(word,0)+1);
        }
        for (Map.Entry entry: filtermap.entrySet()) {
            String word= (String) entry.getKey();
            int startindex=0;
            int flag=0;
            while (( flag=s.indexOf(word,startindex))!=-1){
                Map<String,Integer> wordhashmap=new HashMap<>();
                wordhashmap.put(word,1);
                if (flag+stepsize*words.length>s.length()){
                    break;
                }
                for (int i = 1; i <words.length ; i++) {
                    String currentword=s.substring(flag+i*stepsize,flag+(i+1)*stepsize);
                    if (filtermap.containsKey(currentword)){
                        wordhashmap.put(currentword,wordhashmap.getOrDefault(currentword,0)+1);
                    }else {
                        break;
                    }
                }
                if (isvalid(filtermap,wordhashmap)){
                    result.add(flag);
                }
                startindex=flag+1;
            }
        }

        return result;
    }
    public static boolean isvalid(Map<String,Integer> filtermap,Map<String,Integer> inputmap){
        if (filtermap.size()==inputmap.size()){
            for (Map.Entry entry:inputmap.entrySet()){
                int b=filtermap.getOrDefault(entry.getKey(),0);
                System.out.println("b:"+b);
                int a= (int) entry.getValue();
                System.out.println("a:"+a);
                if ((int)entry.getValue()!=(int)filtermap.getOrDefault(entry.getKey(),0)){
                    return false;
                }
            }
            return true;
        }
        return false;
    }
